import {Image, Select} from "antd";
import {NumberFormatter} from "@/utils/Format";
import {getPermissionOptions, Manager, walletProps} from "@/model/interface.ts";
import {useState} from "react";
import usePostWalletMutate from "@/modules/wallet/function/postMutate.ts";
import {IDelete} from "@/assets";
import {swalAlert} from "@/hooks/swalAlert.ts";
import {typeAlert} from "@/utils";
import {removeMemberFromGroup} from "@/modules/chat/function/chats.ts";
import {useUserStore} from "@/modules/authentication/store/user.ts";

interface props {
	manager: Manager
	wallet?: walletProps
	isOwner: boolean
}

interface options {
	value: string
	label: string
}

const CardManager: React.FC<props> = ({manager, wallet, isOwner}) => {

	const {user} = useUserStore.getState().user
	const {changePermission, removeManager} = usePostWalletMutate()
	const [optionsPermission] = useState<options[]>(getPermissionOptions())
	const [defaultPermission] = useState<options>({
		label: manager.permission,
		value: manager.permission
	})
	const handleChangePermissionClick = (value: { value: string; label: React.ReactNode }) => {
		const data = {
			userId: manager.user.id,
			walletId: wallet?.id,
			permission: value.value
		}
		changePermission(data)
	};


	const onClickRemoveManager = () => {
		swalAlert({
			type: typeAlert.info,
			title: `Do you want remove this manager!!`,
			message: "You will also delete all of its transactions, budgets ,bills of this manager, and this action cannot be undone.",
			showCancel: true,
			optional: {cancelButtonColor: "#3952c6", confirmButtonColor: "#b82126"},
			thenFunc: (async (result) => {
				if (result.isConfirmed) {
					if (wallet) {
						const data = {
							walletId: wallet?.id,
							userId: manager.user.id
						}

						removeManager(data)
						await removeMemberFromGroup(wallet?.id, manager.user)
					}
				}
			})
		})
	}

	return <>
		<div className={`flex-between gap-8 w-full mx-auto mt-8 rounded-2xl p-8 border border-bodydark`}>
			<Image
				width={70}
				height={70}
				src="error"
				className={`rounded-full`}
				fallback="data:image/png;base64,"
			/>
			<div className={`grid grid-cols-5 text-center gap-6 `}>
				<span className={`col-span-1 font-bold text-lg`}>Name</span>
				<span className={`col-span-1 font-bold text-lg text-nowrap`}>Total spent</span>
				<span className={`col-span-2 font-bold text-lg text-nowrap`}>Total transaction</span>
				<span className={`col-span-1 font-bold text-lg text-nowrap`}>Permission</span>
				<span className={`col-span-1`}>{manager.user.id === user.id ? "You" : manager.user.username}</span>
				<span className={`col-span-1`}><NumberFormatter number={manager.totalAmount}/></span>
				<span className={`col-span-2`}>{manager.totalTransaction}</span>
				<Select
					className={`col-span-1`}
					labelInValue
					defaultValue={defaultPermission}
					style={{width: 120}}
					onChange={handleChangePermissionClick}
					options={optionsPermission}
				/>
			</div>
			<div>
				{
					isOwner &&
                    <button onClick={onClickRemoveManager}
                            className={` text-white py-2 px-4 rounded-lg hover:scale-110 duration-300 hover:animate-wiggle text-wrap bg-red-500`}>
                        <IDelete color={"#ffffff"}/></button>
				}
			</div>

		</div>
	</>
}

export default CardManager